import React, { useState, useEffect} from 'react'

function App() {
  const [message, setMessage] = useState ("")

  useEffect (() => {
    fetch('/chat').then(
      res => res.json()
    ).then(
      data => {
        setMessage(data.message)
        console.log(data)
      })
      .catch(error => console.error('Error fetching message:', error));
  }, [])
  return (
    <div>
      {message ? (
        <p>{message}</p>  // Display the message here
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}


export default App
