import React, { useState, useEffect} from 'react'

function App() {
  const [message, setMessage] = useState ("")

  useEffect (() => {
    fetch('/chat').then(
      res => res.json()
    ).then(
      data => {
        setMessage(data.message)
        console.log(data)
      })
  }, [])

  const format_message = (text) => {
    return text.split('\n').map((line, index) => (
      <span key={index}>
        {line}
        <br />
        </span>
    ))
  }

  return (
    <div>
      {message ? (
        <p>{format_message(message)}</p> 
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}


export default App
